import React,{ useEffect, useState } from 'react';
import axios from 'axios';
import { Button } from 'react-bootstrap';
import TodoItem from './components/todoitems';


function App() {
  const [todolist, settodo] = useState([]);

  useEffect( () => {
    axios.get('todos')
      .then(res => {
        settodo(res.data);
        console.log('running')
      })
      .catch(err => {
        console.log(err)
      })

  },[])
  const deleteitem = (id) => {
    axios.delete('todo/'+ id)
    .then(res => {
      settodo(todolist.filter(item => item.id !== id))
    })
    .catch(err => console.log(err))
  }
  return (
    <div>
      <Button style={{marginLeft: '50%', marginTop: '10px'}}>Add TODO Item</Button>

        {
          todolist.map(data =>{
            <TodoItem data={data}  deleteitem={() => console.log('enter delete')}/>
          } 
            )
        }
    </div>
  );
}

export default App;
