import React,{ useEffect, useState } from 'react';
import axios from 'axios';
import { Card, CardColumns, Button, ButtonToolbar, Row} from 'react-bootstrap';


function App() {
  const [todolist, settodo] = useState([]);

  useEffect( () => {
    axios.get('todos')
      .then(res => {
        settodo(res.data);
        console.log('running')
      })
      .catch(err => {
        console.log(err)
      })

  },[])
  const deleteitem = (id) => {
    axios.delete('todo/'+ id)
    .then(res => {
      settodo(todolist.filter(item => item.id !== id))
    })
    .catch(err => console.log(err))
  }
  return (
    <div>
      <CardColumns>
        {
          todolist.map(data =>{
            return (
              <Card bg="white" text='grey' border="primary" style={{width: '30rem'}} key={data.id}>
                <Card.Body>{data.title}</Card.Body>
                <Card.Text style={{textAlign: 'center'}}>{data.description}</Card.Text>
                <Card.Footer>
                  <Row>
                    <Button variant="outline-primary" size="sm" style={{marginRight: "10px"}}>Edit</Button>
                    <Button variant="outline-danger"  size="sm" onClick={() => deleteitem(data.id)} style={{alignSelf: 'right'}}> Delete</Button>
                  </Row>
                </Card.Footer>
              </Card>

            )
          } 
            )
        }
        </CardColumns>
    </div>
  );
}

export default App;
