import React,{ useEffect, useState } from 'react';
import axios from 'axios';
import { Card, CardColumns, Button} from 'react-bootstrap';


function App() {
  const [todolist, settodo] = useState([]);

  useEffect( () => {
    axios.get('todos')
      .then(res => {
        settodo(res.data);
        console.log('running')
      })
      .catch(err => {
        console.log(err)
      })

  },[todolist])
  const deleteitem = (id) => {
    axios.delete('todo/'+ id)
    .then(res => {
      settodo(todolist.filter(item => item.id != id))
    })
    .catch(err => console.log(err))
  }
  return (
    <div>
      <CardColumns>
        {
          todolist.map(data =>{
            return (
              <Card bg="secondary" text='white' border="primary" style={{width: '30rem'}} key={data.id}>
                <Card.Body>{data.title}</Card.Body>
                <Card.Text style={{textAlign: 'center'}}>{data.description}</Card.Text>
                <Card.Footer>
                  <div>
                    <Button variant="primary" style={{alignSelf: 'center'}}>Edit</Button>
                    <Button variant="danger"  onClick={() => deleteitem(data.id)}> Delete</Button>
                  </div>
                </Card.Footer>
              </Card>

            )
          } 
            )
        }
        </CardColumns>
    </div>
  );
}

export default App;
